import { StatusBar } from 'expo-status-bar';
import React, { useState } from "react";
import {
  Image, 
  StyleSheet, 
  Text, 
  View, 
  TextInput, 
  TouchableOpacity,
  ScrollView 
} from "react-native";
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { SafeAreaView } from "react-native-safe-area-context";
import Flashcard from './flashcard';
import CarouselCards from './carouselCards';




const Tab = createBottomTabNavigator();

const Stack = createNativeStackNavigator();
const what={frontContent: 'Alleniga1', backContent: 'Macaspac1'};



//searchbar

const SearchBar = () => {
  return (
    <View style={styles.Search}>
      <View style={styles.SearchBar}>
        <TextInput style={{width:'85%'}} placeholder='Search'/>
        <TouchableOpacity onPress={()=>{}}>

        </TouchableOpacity>  
      </View>
    </View>
  );
};

const Homepage = () => {
  const [sets, setSets] = useState([]);
  const [selectedSet, setSelectedSet] = useState(null);
  const [flashcards, setFlashcards] = useState([]);
  const [newSetName, setNewSetName] = useState('');
  const [newQuestion, setNewQuestion] = useState('');
  const [newAnswer, setNewAnswer] = useState('');

  const addSet = () => {
    if (newSetName) {
      setSets([...sets, { name: newSetName, flashcards: [] }]);
      setNewSetName('');
    }
  };

  const addFlashcardToSet = () => {
    if (selectedSet && newQuestion && newAnswer) {
      const updatedSets = sets.map((set) =>
        set === selectedSet
          ? { ...set, flashcards: [...set.flashcards, { question: newQuestion, answer: newAnswer }] }
          : set
      );
      setSets(updatedSets);
      setSelectedSet(null);
      setNewQuestion('');
      setNewAnswer('');
    }
  };

  return (
    <View style={styles.HomePageUI}>
      <ScrollView>
        <Text style={styles.heading}>Flashcard Sets</Text>
        {sets.map((set, index) => (
          <TouchableOpacity
            key={index}
            style={styles.flashcardSet}
            onPress={() => setSelectedSet(set)}
          >
            <Text>{set.name}</Text>
          </TouchableOpacity>
        ))}
      </ScrollView>

      {selectedSet && (
        <View style={styles.addFlashcardContainer}>
          <Text style={styles.heading}>Add Flashcard to Set: {selectedSet.name}</Text>
          <TextInput
            style={styles.input}
            placeholder="New Question"
            value={newQuestion}
            onChangeText={(text) => setNewQuestion(text)}
          />
          <TextInput
            style={styles.input}
            placeholder="New Answer"
            value={newAnswer}
            onChangeText={(text) => setNewAnswer(text)}
          />
          <TouchableOpacity style={styles.addButton} onPress={addFlashcardToSet}>
            <Text style={styles.addButtonText}>Add Flashcard</Text>
          </TouchableOpacity>
        </View>
      )}

      <View style={styles.addFlashcardContainer}>
        <Text style={styles.heading}>Add Flashcard Set</Text>
        <TextInput
          style={styles.input}
          placeholder="New Set Name"
          value={newSetName}
          onChangeText={(text) => setNewSetName(text)}
        />
        <TouchableOpacity style={styles.addButton} onPress={addSet}>
          <Text style={styles.addButtonText}>Add Set</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};
function Profile() {
  return (
    <SafeAreaView style={styles.ProfileContainer}>
      <ScrollView showsVerticalScrollIndicator={false}>
        <View style={styles.ProfileTitleBar}>
        </View>

        <View style={{ alignSelf: 'center' }}>
          <View style={styles.profileImage}>

            <Image source={require('./assets/vhilly.jpg')} style={styles.image} resizeMode="center" />
          </View>
        </View>

        <View style={styles.profileInfo}>
          <Text style = {[styles.Username,{fontWeight:'200',fontSize:26}]}>Vhilly Manalansang</Text>
          <Text style = {[styles.Username,{color:'#AEB5BC',fontSize:12}]}>manalansang.vhilly@ue.edu.ph</Text>
          <Text style = {[styles.Profileflashcard,{marginTop:32,fontSize:26,fontWeight:'bold'}]}>Flashcards</Text>
        </View>

      </ScrollView>
    </SafeAreaView>
  );
}

function Libraries(){
  const Flashcard = () => {
    return (
      <View style={styles.flashcardContainer}>
        <Flashcard frontContent="Alleniga2" backContent="Macaspac2" />
      </View>
    );
  };
}

function Tabs() {
  return (
    <Tab.Navigator
      initialRouteName="Home"
      screenOptions={{
        header: ({ navigation }) => (
          <View style={styles.TopHeader}>
            <Text style={styles.headerText}>Grove Card</Text>
            <SearchBar />
          </View>
        ),
      }}
    >

      <Tab.Screen
        name="Home"
        component={Flashcard}
        initialParams={what}
      />
      <Tab.Screen name="Libraries" component={Libraries} />
      <Tab.Screen name="Profile" component={Profile} />
      <Tab.Screen name="Carousel" component={CarouselCards} />
    </Tab.Navigator>
  );
}

function SignIn1({ navigation }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    //ADMIN THINGZZZZ
    const user1 = 'vhilly';
    const pass1 = 'pogi';

    if (username === user1 && password === pass1) {
      alert('Login successful!');

      navigation.replace('Tabs');
    } else {

      alert('Invalid username or password');
    }
  };

  return (
    <View style={styles.SignInUI}>
      <View style={styles.LoginUI}>
        <Text style={styles.SignIn}>Sign In</Text>

        <View style={styles.Component1}>
          <TextInput
            style={styles.Username1}
            placeholder="Username"
            value={username}
            onChangeText={(text) => setUsername(text)}
            placeholderTextColor="rgba(0,0,0,0.45)"
          />
        </View>

        <View style={styles.Component1}>
          <TextInput
            style={styles.Password1}
            placeholder="Password"
            value={password}
            placeholderTextColor="rgba(0,0,0,0.45)"
            secureTextEntry={true}
            onChangeText={(text) => setPassword(text)}
          />
        </View>
      </View>

      <TouchableOpacity style={styles.LoginButton} onPress={handleLogin}>
        <Text style={styles.LogIn}>Log In</Text>
      </TouchableOpacity>
    </View>
  );
}


//NAVIGATIONZZZ
const App=()=> {
  return (
    <NavigationContainer>
      <Stack.Navigator screenOptions={{ headerShown: false }}>
        <Stack.Screen name="SignIn" component={SignIn1} />
      <Stack.Screen name="Tabs" component={Tabs} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}


