import { StatusBar } from 'expo-status-bar';
import React, { useState } from "react";
import {
  Image, 
  StyleSheet, 
  Text, 
  View, 
  TextInput, 
  TouchableOpacity,
  ScrollView,
  FlatList  
} from "react-native";
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { SafeAreaView } from "react-native-safe-area-context";
import Flashcard from './flashcard';
import CarouselCards from './carouselCards';




const Tab = createBottomTabNavigator();

const Stack = createNativeStackNavigator();



//decj
const decks = [
  {
    id: 1,
    name: 'GYATT Flashcards',
    flashcards: [
      { id: 1, frontContent: 'Full name ni allen?', backContent: 'BOGART GYATT MACASPAC' },
      { id: 2, frontContent: 'huh?', backContent: 'huhtdog kwento mo sa pagong' },

    ],
  },
  {
    id: 2,
    name: 'Facts',
    flashcards: [
      { id: 1, frontContent: 'shibal', backContent: 'uwu' },
      { id: 2, frontContent: 'a', backContent: 'b' },

    ],
  },

];


//searchbar

const SearchBar = () => {
  return (
    <View style={styles.Search}>
      <View style={styles.SearchBar}>
        <TextInput style={{width:'85%'}} placeholder='Search'/>
        <TouchableOpacity onPress={()=>{}}>

        </TouchableOpacity>  
      </View>
    </View>
  );
};

function Homepage({ navigation }) {
  const [selectedDeck, setSelectedDeck] = useState(decks[0]);
  const [newDeckName, setNewDeckName] = useState('');
  const [newFlashcardFront, setNewFlashcardFront] = useState('');
  const [newFlashcardBack, setNewFlashcardBack] = useState('');

  const handleDeckPress = (deck) => {
    setSelectedDeck(deck);
  };

  const handleButtonPress = () => {
    navigation.navigate('AnotherScreen');
  };

  const handleAddDeck = () => {
    // Add new deck with the provided name
    const newDeck = {
      id: decks.length + 1,
      name: newDeckName,
      flashcards: [],
    };

    // Update the decks state
    setDecks([...decks, newDeck]);

    // Clear the input field
    setNewDeckName('');
  };

  const handleAddFlashcard = () => {
    // Add new flashcard to the selected deck
    const newFlashcard = {
      id: selectedDeck.flashcards.length + 1,
      frontContent: newFlashcardFront,
      backContent: newFlashcardBack,
    };

    // Update the selected deck's flashcards
    setSelectedDeck({
      ...selectedDeck,
      flashcards: [...selectedDeck.flashcards, newFlashcard],
    });

    // Clear the input fields
    setNewFlashcardFront('');
    setNewFlashcardBack('');
  };

  return (
    <ScrollView showsVerticalScrollIndicator={false} style={{ flex: 1 }}>
      <View style={styles.HomePageUI}>
        <FlatList
          data={decks}
          keyExtractor={(item) => item.id.toString()}
          renderItem={({ item }) => (
            <TouchableOpacity onPress={() => handleDeckPress(item)} style={styles.deckContainer}>
              <Text style={styles.deckTitle}>{item.name}</Text>
            </TouchableOpacity>
          )}
        />

        <Text style={styles.deckTitle}>{selectedDeck.name}</Text>
        <FlatList
          data={selectedDeck.flashcards}
          keyExtractor={(item) => item.id.toString()}
          renderItem={({ item }) => (
            <Flashcard route={{ params: { frontContent: item.frontContent, backContent: item.backContent } }} />
          )}
        />

        {/* Form for adding a new deck */}
        <View style={styles.formContainer}>
          <Text style={styles.formTitle}>Add a New Deck</Text>
          <TextInput
            style={styles.input}
            placeholder="Deck Name"
            value={newDeckName}
            onChangeText={setNewDeckName}
          />
          <TouchableOpacity style={styles.addButton} onPress={handleAddDeck}>
            <Text style={styles.addButtonText}>Add Deck</Text>
          </TouchableOpacity>
        </View>

        {/* Form for adding a new flashcard */}
        <View style={styles.formContainer}>
          <Text style={styles.formTitle}>Add a New Flashcard</Text>
          <TextInput
            style={styles.input}
            placeholder="Front Content"
            value={newFlashcardFront}
            onChangeText={setNewFlashcardFront}
          />
          <TextInput
            style={styles.input}
            placeholder="Back Content"
            value={newFlashcardBack}
            onChangeText={setNewFlashcardBack}
          />
          <TouchableOpacity style={styles.addButton} onPress={handleAddFlashcard}>
            <Text style={styles.addButtonText}>Add Flashcard</Text>
          </TouchableOpacity>
        </View>

        <TouchableOpacity style={styles.addButton} onPress={handleButtonPress}>
          <Text style={styles.addButtonText}>ADD DECK</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
}

function Profile() {
  return (
    <SafeAreaView style={styles.ProfileContainer}>
      <ScrollView showsVerticalScrollIndicator={false}>
        <View style={styles.ProfileTitleBar}>
        </View>

        <View style={{ alignSelf: 'center' }}>
          <View style={styles.profileImage}>

            <Image source={require('./assets/vhilly.jpg')} style={styles.image} resizeMode="center" />
          </View>
        </View>

        <View style={styles.profileInfo}>
          <Text style = {[styles.Username,{fontWeight:'200',fontSize:26}]}>Vhilly Manalansang</Text>
          <Text style = {[styles.Username,{color:'#AEB5BC',fontSize:12}]}>manalansang.vhilly@ue.edu.ph</Text>
          <Text style = {[styles.Profileflashcard,{marginTop:32,fontSize:26,fontWeight:'bold'}]}>Flashcards</Text>
        </View>

      </ScrollView>
    </SafeAreaView>
  );
}

function Libraries(){

}

function Tabs() {
  return (
    <Tab.Navigator
      initialRouteName="Home"
      screenOptions={{
        header: ({ navigation }) => (
          <View style={styles.TopHeader}>
            <Text style={styles.headerText}>Grove Card</Text>
            <SearchBar />
          </View>
        ),
      }}
    >

      <Tab.Screen name="Home" component={Homepage}/>
      <Tab.Screen name="Libraries" component={Libraries} />
      <Tab.Screen name="Profile" component={Profile} />
      <Tab.Screen name="Carousel" component={CarouselCards} />
    </Tab.Navigator>
  );
}

function SignIn1({ navigation }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    //ADMIN THINGZZZZ
    const user1 = 'vhilly';
    const pass1 = 'pogi';

    if (username === user1 && password === pass1) {
      alert('Login successful!');

      navigation.replace('Tabs');
    } else {

      alert('Invalid username or password');
    }
  };

  return (
    <View style={styles.SignInUI}>
      <View style={styles.LoginUI}>
        <Text style={styles.SignIn}>Sign In</Text>

        <View style={styles.Component1}>
          <TextInput
            style={styles.Username1}
            placeholder="Username"
            value={username}
            onChangeText={(text) => setUsername(text)}
            placeholderTextColor="rgba(0,0,0,0.45)"
          />
        </View>

        <View style={styles.Component1}>
          <TextInput
            style={styles.Password1}
            placeholder="Password"
            value={password}
            placeholderTextColor="rgba(0,0,0,0.45)"
            secureTextEntry={true}
            onChangeText={(text) => setPassword(text)}
          />
        </View>
      </View>

      <TouchableOpacity style={styles.LoginButton} onPress={handleLogin}>
        <Text style={styles.LogIn}>Log In</Text>
      </TouchableOpacity>
    </View>
  );
}


